import { withRouter } from 'next/router'
import Link from 'next/link'

const ActiveLink = ({ children, router, as,href }) => {
    const handleClick = (e) => {
        e.preventDefault()
        window.location.href=as;
    }

    return (
        <Link  as={as} href={href} onClick={handleClick} >
            <a> {children}</a>
        </Link>
    )
}

export default withRouter(ActiveLink)
